import { useEffect, useState } from 'react';
import './App.css';
import Main from './components/Main';
function App() {

  const [reload , setreload]=useState(false);

  useEffect(() => {
    window.addEventListener( "beforeunload", ( () => { setreload(true) ; console.log("REFRESHED") ; } )  );
  },[])

  return (
    <div className="App">
      <Main PageReload={reload} />
    </div>
  );
}

export default App;
