      const deletestudent = async (email) => {
        const db = await openDB('retail',1);
        await db.delete('details', email);

        data = await db.getAll('details') ;
        console.log(data);
        setlists1(data);
        console.log(lists1);
      }

      const handletogglemark = (email) => {
          if( email === togglemark )
          {
            settogglemark("");
          }
          else
          {
          settogglemark(email);
          }
      }

      const updatemark = async (e, task) => {
        e.preventDefault() ;
        console.log(updatedmark)
        console.log(task);

        const db = await openDB('retail',1);
        db.put( 'details', { name: task.name, email: task.email, dob: task.dob, rollno: task.rollno, mark: updatedmark } )
        .catch((err) => { console.log(err) } ) ;

        data = await db.getAll('details') ;
        setlists1(data);


      }

      const getlatlog = async (lat, long) => {
          setlatitude(lat);
          setlongitude(long);
          setlocated( latitude && longitude ? (true) : (false) ) ;
      }

      const handlefilesubmit = async(e) => {
        
        for (let index = 0; index < e.target.files.length; index++) {
          image_array.push(e.target.files[index])
        }
        console.log(image_array)
        setimages(image_array)
      }

    const handlesubmit =async (e) => {
        e.preventDefault() ;

        if( located )
        {
          const db = await openDB('retail',1);
          db.add( 'details', { 
            name: name, 
            email: email, 
            contact_number1: contact1,
            contact_number2: contact2,
            retail_size: retailsize,
            it_automation: itautomation,
            no_of_mobile: mobile ? (nomobile) : (0) ,
            no_of_tab: tab ? (notab) : (0) ,
            no_of_computer: computer ? (nocomputer) : (0),
            no_of_printer: printer ? (noprinter) : (0),
            no_of_scanner: scanner ? (noscanner) : (0),
            latitude: latitude,
            longitude: longitude,
            photos: images
          } )
          .catch((err) => { console.log(err) } ) ;
          data = await db.getAll('details') ;
          console.log(data);
          setlists1(data);
          console.log(lists1);
        }
        else
        {
          alert('Please select your location');
        }

        

    }
    
  return (
      <div>
          <form className="getdata" onSubmit={handlesubmit}>

            <span className="heading">Enter Retail Information</span>

          <div className="lbl_ipt">    
          <label className="label">Name*</label>
          <input className="input" type='text' onChange={ (e) => { setname(e.target.value) } } required />
          </div>

          <div className="lbl_ipt">
          <label className="label">E-Mail*</label>
          <input className="input" type='email' onChange={ (e) => { setemail(e.target.value) } } required />
          </div>

          <div className="lbl_ipt">
          <label className="label contact_font">Contact Number 1*</label>
          <input className="input" type='number' onChange={ (e) => { setcontact1(e.target.value) } } required />
          </div>

          <div className="lbl_ipt">
          <label className="label contact_font">Contact Number 2</label>
          <input className="input" type='number' onChange={ (e) => { setcontact2(e.target.value) } } />
          </div>

          <div className="lbl_ipt">
          <label className="label">Retail Size*</label>
          <input className="input" type='number' placeholder="in sq feet area" onChange={ (e) => { setretailsize(e.target.value) } } required />
          </div>

          <div className="lbl_ipt">
          <label className="label">IT Automation*</label>
          <select value={itautomation} onChange={ (e) => { setitautomation(e.target.value) } } required>
            <option value="Automated">Automated</option>
            <option value="Non-Automated">Non-Automated</option>
          </select>
          </div>

          <div className="slt_e-comm">
          <label className="label">Types of Machine</label>
                
          <div className="align_chbox_lbl">
          
          <label className="machine_css" ><input type="checkbox" value={mobile} onChange={ (e) => { setmobile(e.currentTarget.checked) }  } />
             mobile</label>
            </div>
            { mobile &&   <input className="input" type='number' onChange={ (e) => { setnomobile(e.target.value) } } placeholder="Number of Devices" required={mobile} /> }
          
              <br/>  
          <div className="align_chbox_lbl">
          <label className="machine_css"  ><input type="checkbox" value={tab} onChange={ (e) => { settab(e.currentTarget.checked) }  } />
             tab</label>
            </div>
            { tab &&   <input className="input" type='number' onChange={ (e) => { setnotab(e.target.value) } } placeholder="Number of Devices" required={tab} /> }
          
            <br/>
          <div className="align_chbox_lbl">
          <label className="machine_css"  > <input type="checkbox" value={computer} onChange={ (e) => { setcomputer(e.currentTarget.checked) }  } />
             computer</label>
            </div>
            { computer &&   <input className="input" type='number' onChange={ (e) => { setnocomputer(e.target.value) } } placeholder="Number of Devices" required={computer} /> }
          
            <br/>
          <div className="align_chbox_lbl">
          <label className="machine_css"  ><input type="checkbox" value={printer} onChange={ (e) => { setprinter(e.currentTarget.checked) }  } />
             printer</label>
            </div>
            { printer &&   <input className="input" type='number' onChange={ (e) => { setnoprinter(e.target.value) } } placeholder="Number of Devices" required={printer} /> }
          
            <br/>
          <div className="align_chbox_lbl">
          <label className="machine_css"  ><input type="checkbox" value={scanner} onChange={ (e) => { setscanner(e.currentTarget.checked) }  } />
             scanner</label>
            </div>
            { scanner &&   <input className="input" type='number' onChange={ (e) => { setnoscanner(e.target.value) } } placeholder="Number of Devices" required={scanner} /> }
          
          <br />
        </div>

        <GoogleMaps AddLatLong={ getlatlog } />

        <div className="lbl_ipt">
        <label className="label">Photo*</label>
          <input className="input" type='file' multiple onChange={ (e) => { handlefilesubmit(e) } } required />
          </div>

          <label className="label">{error}</label>

          <div className="lbl_ipt form_btn">
          <button type="submit" className="form_button">Submit</button>
          </div>

          </form>

          <div className="display_students">

           { name } <br />
           { email }<br />
           { contact1 }<br />
           { contact2 }<br />
           { retailsize }<br />
           { itautomation }<br />
           { located && <p>LOCATION SPOTTED</p> }
           { image_array }<br />
           { latitude } <br/>
           { longitude } <br/>
           { mobile && nomobile } <br />
           { tab && notab } <br />
           { computer && nocomputer } <br />
           { printer && noprinter } <br />
           { scanner && noscanner } <br />
           
          </div>

      </div>
  ) 
};

export default Main;
