                  mysql_sync: false,
                };
                db.add("details", indexeddb_data_sync_false).then((res) => {
                  console.log(res);
                });
                seterror("YOU ARE OFFLINE, Retail Information SAVED, Data will be validated once your are online");
                setsubmitted(true);

            });
        // } 
        // else {
        //   console.log("OFFLINE");

        //   const indexeddb_data_sync_false = {
        //     name: name,
        //     email: email,
