      }
    } else {
      seterror("Please select your location");
    }
  };

  return (
    <div>
      <form className="getdata" onSubmit={handlesubmit}>
        <span className="heading">Enter Retail Information</span>

        <div className="lbl_ipt">
          <label className="label">Name*</label>
          <input
            className="input"
            type="text"
            onChange={(e) => {
              setname(e.target.value);
            }}
            required
          />
        </div>

        <div className="lbl_ipt">
          <label className="label">E-Mail*</label>
          <input
            className="input"
            type="email"
            onChange={(e) => {
              setemail(e.target.value);
            }}
            required
          />
        </div>

        <div className="lbl_ipt">
          <label className="label contact_font">Contact Number 1*</label>
          <input
            className="input"
            placeholder="10 digit"
            type="tel"
            pattern="[0-9]{10}"
            onChange={(e) => {
              setcontact1(Number(e.target.value));
            }}
            required
          />
        </div>

        <div className="lbl_ipt">
          <label className="label contact_font">Contact Number 2</label>
          <input
            className="input"
            placeholder="10 digit"
            type="tel"
            pattern="[0-9]{10}"
            onChange={(e) => {
              setcontact2(Number(e.target.value));
            }}
          />
        </div>

        <div className="lbl_ipt">
          <label className="label">Retail Size*</label>
          <input
            className="input"
            type="number"
            placeholder="in sq feet area"
            onChange={(e) => {
              setretailsize(Number(e.target.value));
            }}
            required
          />
        </div>

        <div className="lbl_ipt">
          <label className="label">IT Automation*</label>
          <select
            value={itautomation}
            onChange={(e) => {
              setitautomation(e.target.value);
            }}
            required
          >
            <option value="Automated">Automated</option>
            <option value="Non-Automated">Non-Automated</option>
          </select>
        </div>

        <div className="slt_e-comm">
          <label className="label">Types of Machine</label>

          <div className="align_chbox_lbl">
            <label className="machine_css">
              <input
                type="checkbox"
                value={mobile}
                onChange={(e) => {
                  setmobile(e.currentTarget.checked);
                }}
              />
              mobile
            </label>
          </div>
          {mobile && (
            <input
              className="input"
              type="number"
              onChange={(e) => {
                setnomobile(Number(e.target.value));
              }}
              placeholder="Number of Devices"
              required={mobile}
            />
          )}

          <br />
          <div className="align_chbox_lbl">
            <label className="machine_css">
              <input
                type="checkbox"
                value={tab}
                onChange={(e) => {
                  settab(e.currentTarget.checked);
                }}
              />
              tab
            </label>
          </div>
          {tab && (
            <input
              className="input"
              type="number"
              onChange={(e) => {
                setnotab(Number(e.target.value));
              }}
              placeholder="Number of Devices"
              required={tab}
            />
          )}

          <br />
          <div className="align_chbox_lbl">
            <label className="machine_css">
              {" "}
              <input
                type="checkbox"
                value={computer}
                onChange={(e) => {
                  setcomputer(e.currentTarget.checked);
                }}
              />
              computer
            </label>
          </div>
          {computer && (
            <input
              className="input"
              type="number"
              onChange={(e) => {
                setnocomputer(Number(e.target.value));
              }}
              placeholder="Number of Devices"
              required={computer}
            />
          )}

          <br />
          <div className="align_chbox_lbl">
            <label className="machine_css">
              <input
                type="checkbox"
                value={printer}
                onChange={(e) => {
                  setprinter(e.currentTarget.checked);
                }}
              />
              printer
            </label>
          </div>
          {printer && (
            <input
              className="input"
              type="number"
              onChange={(e) => {
                setnoprinter(Number(e.target.value));
              }}
              placeholder="Number of Devices"
              required={printer}
            />
          )}

          <br />
          <div className="align_chbox_lbl">
            <label className="machine_css">
              <input
                type="checkbox"
                value={scanner}
                onChange={(e) => {
                  setscanner(e.currentTarget.checked);
                }}
              />
              scanner
            </label>
          </div>
          {scanner && (
            <input
              className="input"
              type="number"
              onChange={(e) => {
                setnoscanner(Number(e.target.value));
              }}
              placeholder="Number of Devices"
              required={scanner}
            />
          )}

          <br />
        </div>

        <GoogleMaps AddLatLong={getlatlog} />

        <CameraModule AddLiveImage={HandleLiveCapture} />

        <div>
          { live_capture && 
          <img src={ live_capture } alt="live_capture" />
          }
        </div>

        <div className="lbl_ipt">
          <label className="label">Photo</label>
          <input
            className="input"
            type="file"
            onChange={(e) => { setimage_upload(URL.createObjectURL(e.target.files[0])) ; setlive_capture() ; setblobimage(e.fileInput.files[0]) ;  }}
          />
        </div>

        <div>
        { image_upload && 
          <img src={image_upload} className="image_upload_style" alt="image_upload" />
        }
        </div>

        <label className={`label ${submitted ? "green" : "error_display"} `}>
          {error}
        </label>

        <div className="lbl_ipt form_btn">
          <button type="submit" className="form_button">
            Submit
          </button>
        </div>
      </form>

      <div>
        {/* {lists1.length > 0 ? (
          <>
          <h1>LIST1</h1>
            {lists1.map((task) => (
              <div key={task.email}>
                <p> Name - {task.name} </p>
                <p> Email - {task.email} </p>
                <p> SYNC - {task.mysql_sync && "true"} </p>
              </div>
            ))}
          </>
        ) : (
          "NO DATA "
        )} */}

          {/* {deletefromidb.length > 0 ? (
          <>
          <h1>IDB DELETE</h1>
          { deletefromidb.map( (item) => (
            <div key={item}>
            <p>{item.email}  </p>
            <p>{! item.mysql_sync && "false"}  </p>
            </div>
          ) ) }
          </> 
          ) : (
            "NO IDB " )} */}

          {/* { fetch_Data_from_MySQL.length > 0 ? (
             <>
             <h1>MYSQL DATA</h1>
             { fetch_Data_from_MySQL.length + ' DATAS' }
             { fetch_Data_from_MySQL.map( (item, index) => {
               return <div key={index}>
                 <p>{item.name}</p>
                 <p>{item.email}</p>
               </div>
             } ) }
             </>
             ) : ( "NO MYSQL DATA " ) }  */}

             <button onClick={(async() => {
              // await getting_data_from_idb();
              await update_mysql_with_idb(); 
                    // delete_from_idb();
              } )}>
               FETCH
             </button>

          

       
        {name} <br />
        {email}
        <br />
        {contact1}
        <br />
        {contact2}
        <br />
        {retailsize}
        <br />
        {itautomation}
        <br />
        {located && <p>LOCATION SPOTTED</p>}
        <br />
        {latitude} <br />
        {longitude} <br />
        {mobile && nomobile} <br />
        {tab && notab} <br />
        {computer && nocomputer} <br />
        {printer && noprinter} <br />
        {scanner && noscanner} <br /> 
      </div>
    </div>
  );
};

export default Main;
