import React, { useState } from "react";
import { useEffect } from "react";
import { openDB, deleteDB, wrap, unwrap } from "idb";
import './style.css';
import GoogleMaps from "./GoogleMaps";

const Main =() => {

  console.log("1ST line");
    const [name, setname] = useState("") ;
    const [email, setemail] = useState("") ;
    const [contact1, setcontact1] = useState() ;
    const [contact2, setcontact2] = useState() ;
    const [retailsize, setretailsize] = useState("") ;
    const [itautomation, setitautomation] = useState("Automated") ;

    const [mobile, setmobile] = useState(false);
    const [tab, settab] = useState(false);
    const [computer, setcomputer] = useState(false);
    const [printer, setprinter] = useState(false);
    const [scanner, setscanner] = useState(false);

    const [nomobile, setnomobile] = useState(0);
    const [notab, setnotab] = useState(0);
    const [nocomputer, setnocomputer] = useState(0);
    const [noprinter, setnoprinter] = useState(0);
    const [noscanner, setnoscanner] = useState(0);

    const [submitted, setsubmitted] = useState(false);
    const [located, setlocated] = useState(false);
    const [latitude, setlatitude] = useState("");
    const [longitude, setlongitude] = useState("");
    const [lists1, setlists1] = useState([]) ; 
    const [updatedmark, setupdatedmark] = useState("");
    const [togglemark, settogglemark] = useState("");
    const [images, setimages] = useState("");
    const [error, seterror] = useState("");
    const [create_fetch_msg, setcreate_fetch_msg] = useState();


    var data="";
    var datas="";
    var image_array=[];

    openDB('retail', 1, {
        upgrade(db) {
          db.createObjectStore('details', { keyPath: 'email' });
        },
      });
      
    //   const start = openDB('retail',1);
    //   data = start.getAll('details') ;
    //     setlists1(data);
        

    //   useEffect ( () => {
    //       const updateval = async () => {
    //           const new_data = await latest_data()  
    //           setlists1(new_data)
    //       }
    //       updateval() 
    //   },[lists1] )

    useEffect ( () => {
      console.log("INSIDE USEEFFECT");
        const getting_data =  async () => {
            const newdata =await latest_data() ;
            setlists1(newdata) ;

        }
        getting_data(); 
        
    } )

      const latest_data = async () => {
        const db = await openDB('retail',1);
        data = await db.getAll('details') ;
        return data ;
      }

      const deletestudent = async (email) => {
        const db = await openDB('retail',1);
        await db.delete('details', email);

        data = await db.getAll('details') ;
        console.log(data);
        setlists1(data);
        console.log(lists1);
      }

      const handletogglemark = (email) => {
          if( email === togglemark )
          {
            settogglemark("");
          }
          else
          {
          settogglemark(email);
          }
      }

      const updatemark = async (e, task) => {
        e.preventDefault() ;
        console.log(updatedmark)
        console.log(task);

        const db = await openDB('retail',1);
        db.put( 'details', { name: task.name, email: task.email, dob: task.dob, rollno: task.rollno, mark: updatedmark } )
        .catch((err) => { console.log(err) } ) ;

        data = await db.getAll('details') ;
        setlists1(data);


      }

      const getlatlog = async (lat, long) => {
          setlatitude(lat);
          setlongitude(long);
          setlocated( latitude && longitude ? (true) : (false) ) ;
      }

      const handlefilesubmit = async(e) => {
        
        for (let index = 0; index < e.target.files.length; index++) {
          image_array.push(e.target.files[index])
        }
        console.log(image_array)
        setimages(image_array)
      }

    const handlesubmit =async (e) => {
        e.preventDefault() ;
        seterror("");
        setsubmitted(false);
        const db = await openDB('retail',1);

        if( located )
        {
          if( await db.get('details',email) )
          {
            seterror("Email ID already registered");
           
          }
          else if( contact1 === contact2 )
          {
            seterror("Both the contact number can't be same")
            
          }
          else if((retailsize <= 0) )
          {
            const temp = "Retail size can't be " +retailsize ;
            seterror(temp);
          }
          else if( !mobile && !tab && !computer && !printer && !scanner  )
          {
            const temp = "Please choose the machine";
            seterror(temp);
          }
          else if(mobile && (nomobile <= 0) )
          {
            const temp = "Number of mobiles can't be " +nomobile ;
            seterror(temp);
          }
          else if(tab && (notab <= 0) )
          {
            const temp = "Number of tabs can't be " +notab ;
            seterror(temp);        
          }
          else if(computer && (nocomputer <= 0) )
          {
            const temp = "Number of computers can't be " +nocomputer ;
            seterror(temp);
            
          }
          else if(printer && (noprinter <= 0) )
          {
            const temp = "Number of printers can't be " +noprinter ;
            seterror(temp);            
          }
          else if(scanner && (noscanner <= 0) )
          {
            const temp = "Number of scanners can't be " +noscanner ;
            seterror(temp);            
          }
          else
          {
            const mysql_data = { 
              name: name, 
              email: email, 
              contact_number1: contact1,
              contact_number2: contact2,
              retail_size: retailsize,
              it_automation: itautomation,
              no_of_mobile: nomobile ,
              no_of_tab: notab ,
              no_of_computer: nocomputer,
              no_of_printer: noprinter,
              no_of_scanner: noscanner,
              latitude: latitude,
              longitude: longitude
            } ;

            const indexeddb_data = { 
              name: name, 
              email: email, 
              contact_number1: contact1,
              contact_number2: contact2,
              retail_size: retailsize,
              it_automation: itautomation,
              no_of_mobile: nomobile ,
              no_of_tab: notab ,
              no_of_computer: nocomputer,
              no_of_printer: noprinter,
              no_of_scanner: noscanner,
              latitude: latitude,
              longitude: longitude,
              mysql_sync: true
            } ;

            //   db.add( 'details',indexeddb_data)
            // .then( () => {
              if(navigator.onLine)
              {
                 fetch("http://127.0.0.1:8000/retail/create/" , {
                  method: 'POST',
                  headers: {
                    'Content-Type': 'application/json'
                  },
                  body: JSON.stringify(mysql_data)
                }).then( (response) => {

                  if( response.status === 400 )
                  {
                      seterror("404 BAD REQUEST, ERROR IN RETAIL DATA") ;
                      const json_data = response.json();
                      console.log("mysql response", json_data);
                      // setcreate_fetch_msg(json_data) ;

                  }
                  else
                  {
                    // Object.keys(json_data).forEach(function(data){
                    //   console.log(data) ;
                    // } )

                    db.add( 'details',indexeddb_data)
                    .then((res) => {
                      console.log(res)
                    })
                    seterror("Retail Information Successfully Submitted") ;
                    setsubmitted(true);
                  }
                })
                .catch( (err) => {
                  console.log(err);
                  seterror(err);
                } )
              }
              else
              {
                  console.log("OFFLINE") ;

                  const indexeddb_data_sync_false = { 
                    name: name, 
                    email: email, 
                    contact_number1: contact1,
                    contact_number2: contact2,
                    retail_size: retailsize,
                    it_automation: itautomation,
                    no_of_mobile: nomobile ,
                    no_of_tab: notab ,
                    no_of_computer: nocomputer,
                    no_of_printer: noprinter,
                    no_of_scanner: noscanner,
                    latitude: latitude,
                    longitude: longitude,
                    mysql_sync: false
                  } ;

                  db.add( 'details',indexeddb_data_sync_false)
                    .then((res) => {
                    console.log(res)
                  })
                  seterror("Retail Information Successfully Submitted") ;
                  setsubmitted(true);

              }
            // } )
            // .catch((err) => { 
            //   console.log(err) ;
            //   seterror(err);
            // } ) ;
            data = await db.getAll('details') ;
            console.log(data);
            setlists1(data);
            console.log(lists1);
          }
        }
        else
        {
          seterror("Please select your location");
        }

        

    }
    
  return (
      <div>
          <form className="getdata" onSubmit={handlesubmit}>

            <span className="heading">Enter Retail Information</span>

          <div className="lbl_ipt">    
          <label className="label">Name*</label>
          <input className="input" type='text' onChange={ (e) => { setname(e.target.value) } } required />
          </div>

          <div className="lbl_ipt">
          <label className="label">E-Mail*</label>
          <input className="input" type='email' onChange={ (e) => { setemail(e.target.value) } } required />
          </div>

          <div className="lbl_ipt">
          <label className="label contact_font">Contact Number 1*</label>
          <input className="input" placeholder="10 digit" type='tel' pattern="[0-9]{10}" onChange={ (e) => { setcontact1(Number(e.target.value)) } } required />
          </div>

          <div className="lbl_ipt">
          <label className="label contact_font">Contact Number 2</label>
          <input className="input" placeholder="10 digit" type='tel' pattern="[0-9]{10}" onChange={ (e) => { setcontact2(Number(e.target.value)) } } />
          </div>

          <div className="lbl_ipt">
          <label className="label">Retail Size*</label>
          <input className="input" type='number' placeholder="in sq feet area" onChange={ (e) => { setretailsize(Number(e.target.value)) } } required />
          </div>

          <div className="lbl_ipt">
          <label className="label">IT Automation*</label>
          <select value={itautomation} onChange={ (e) => { setitautomation(e.target.value) } } required>
            <option value="Automated">Automated</option>
            <option value="Non-Automated">Non-Automated</option>
          </select>
          </div>

          <div className="slt_e-comm">
          <label className="label">Types of Machine</label>
                
          <div className="align_chbox_lbl">
          
          <label className="machine_css" ><input type="checkbox" value={mobile} onChange={ (e) => { setmobile(e.currentTarget.checked) }  } />
             mobile</label>
            </div>
            { mobile &&   <input className="input" type='number' onChange={ (e) => { setnomobile(Number(e.target.value)) } } placeholder="Number of Devices" required={mobile} /> }
          
              <br/>  
          <div className="align_chbox_lbl">
          <label className="machine_css"  ><input type="checkbox" value={tab} onChange={ (e) => { settab(e.currentTarget.checked) }  } />
             tab</label>
            </div>
            { tab &&   <input className="input" type='number' onChange={ (e) => { setnotab(Number(e.target.value)) } } placeholder="Number of Devices" required={tab} /> }
          
            <br/>
          <div className="align_chbox_lbl">
          <label className="machine_css"  > <input type="checkbox" value={computer} onChange={ (e) => { setcomputer(e.currentTarget.checked) }  } />
             computer</label>
            </div>
            { computer &&   <input className="input" type='number' onChange={ (e) => { setnocomputer(Number(e.target.value)) } } placeholder="Number of Devices" required={computer} /> }
          
            <br/>
          <div className="align_chbox_lbl">
          <label className="machine_css"  ><input type="checkbox" value={printer} onChange={ (e) => { setprinter(e.currentTarget.checked) }  } />
             printer</label>
            </div>
            { printer &&   <input className="input" type='number' onChange={ (e) => { setnoprinter(Number(e.target.value)) } } placeholder="Number of Devices" required={printer} /> }
          
            <br/>
          <div className="align_chbox_lbl">
          <label className="machine_css"  ><input type="checkbox" value={scanner} onChange={ (e) => { setscanner(e.currentTarget.checked) }  } />
             scanner</label>
            </div>
            { scanner &&   <input className="input" type='number' onChange={ (e) => { setnoscanner(Number(e.target.value)) } } placeholder="Number of Devices" required={scanner} /> }
          
          <br />
        </div>

        <GoogleMaps AddLatLong={ getlatlog } />

        <div className="lbl_ipt">
        <label className="label">Photo*</label>
          <input className="input" type='file' multiple onChange={ (e) => { handlefilesubmit(e) } } required />
          </div>

          <label className={`label ${ submitted ? 'green' : 'error_display' } `}>{error}</label>

          <div className="lbl_ipt form_btn">
          <button type="submit" className="form_button">Submit</button>
          </div>

          </form>

          <div className="display_students">

            { create_fetch_msg } <br/>

           { name } <br />
           { email }<br />
           { contact1 }<br />
           { contact2 }<br />
           { retailsize }<br />
           { itautomation }<br />
           { located && <p>LOCATION SPOTTED</p> }
           { image_array }<br />
           { latitude } <br/>
           { longitude } <br/>
           { mobile && nomobile } <br />
           { tab && notab } <br />
           { computer && nocomputer } <br />
           { printer && noprinter } <br />
           { scanner && noscanner } <br />
           
          </div>

      </div>
  ) 
};

export default Main;
