import axios from 'axios';
import React, { useEffect, useState } from 'react'
import Table from '../Table/Table';

const List = () => {

  const [list, setlist] = useState([]);

  useEffect ( () => {
    openDB("retail", 1, {
      upgrade(db) { 
        db.createObjectStore("list_data", { keyPath: "email" });
      },
    });
  }, [] );

  useEffect( () => {
    axios.get("http://127.0.0.1:8000/retail/create/")
    .then( (response) => {
      setlist(response.data);
    } )
    .catch( (err) => {
      console.log(err);
    } )
  }, [] );

  return (
    <div>
      <Table list={list}/>
    </div>
  )
}

export default List
