import React from 'react'
import './gmap.css';

const GoogleMaps = () => {

    const mapArea = document.getElementById('map');
const actionBtn = document.getElementById('showMe');
const locationsAvailable = document.getElementById('locationList');
const __KEY = 'AIzaSyCJPmvnJDhrsicjkGs7KkRKhMjmEVxNuc8';
let Gmap;
let Gmarker;
actionBtn.addEventListener('click', e => {
  actionBtn.style.display = "none";
  M.toast({ html: 'fetching your current location', classes: 'rounded' });
  getLocation();
});

getLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(displayLocation, showError, options);
    } else {
      M.toast({ html: "Sorry, your browser does not support this feature... Please Update your Browser to enjoy it", classes: "rounded" });
    }
  }

showError = (error) => {
    mapArea.style.display = "block"
    switch (error.code) {
      case error.PERMISSION_DENIED:
        mapArea.innerHTML = "You denied the request for your location."
        break;
      case error.POSITION_UNAVAILABLE:
        mapArea.innerHTML = "Your Location information is unavailable."
        break;
      case error.TIMEOUT:
        mapArea.innerHTML = "Your request timed out. Please try again"
        break;
      case error.UNKNOWN_ERROR:
        mapArea.innerHTML = "An unknown error occurred please try again after some time."
        break;
    }
  }
  const options = {
    enableHighAccuracy: true
  }

  displayLocation = (position) => {
    const lat = position.coords.latitude;
    const lng = position.coords.longitude;
    console.log( `Current Latitude is ${lat} and your longitude is ${lng}` );
    const latlng = {lat, lng}
    showMap(latlng);
    createMarker(latlng);
    mapArea.style.display = "block";
    getGeolocation(lat, lng)
  }

  showMap = (latlng) => {
    let mapOptions = {
      center: latlng,
      zoom: 17
    };
    Gmap = new google.maps.Map(mapArea, mapOptions);
  }

  createMarker = (latlng) => {
    let markerOptions = {
      position: latlng,
      map: Gmap,
      animation: google.maps.Animation.BOUNCE,
      clickable: true
    };
    Gmarker = new google.maps.Marker(markerOptions);
  }

  return (
    <div class="container">
      <h3>Shipping Address</h3>
      <p>You can click the button below to use your current location as your shipping address</p>
      <div id="map">
      </div>
      <button id="showMe" class="btn">Use My Location</button> 
    </div>
  )
}

export default GoogleMaps
