import React, { useState } from "react";
import { useEffect } from "react";
import { openDB, deleteDB, wrap, unwrap } from "idb";
import "./style.css";
import GoogleMaps from "./GoogleMaps";
import CameraModule from "./CameraModule";

function Main(){
  const [name, setname] = useState("");
  const [email, setemail] = useState("");
  const [contact1, setcontact1] = useState();
  const [contact2, setcontact2] = useState();
  const [retailsize, setretailsize] = useState("");
  const [itautomation, setitautomation] = useState("Automated");

  const [mobile, setmobile] = useState(false);
  const [tab, settab] = useState(false);
  const [computer, setcomputer] = useState(false);
  const [printer, setprinter] = useState(false);
  const [scanner, setscanner] = useState(false);

  const [nomobile, setnomobile] = useState(0);
  const [notab, setnotab] = useState(0);
  const [nocomputer, setnocomputer] = useState(0);
  const [noprinter, setnoprinter] = useState(0);
  const [noscanner, setnoscanner] = useState(0);

  const [submitted, setsubmitted] = useState(false);
  const [located, setlocated] = useState(false);
  const [latitude, setlatitude] = useState("");
  const [longitude, setlongitude] = useState("");
  const [lists1, setlists1] = useState([]);
  const [error, seterror] = useState("");
  const [image_upload, setimage_upload] = useState();
  const [live_capture, setlive_capture] = useState();

  const [deletefromidb, setdeletefromidb] = useState([]);
  const [fetch_Data_from_MySQL ,setfetch_Data_from_MySQL] = useState([]);

  useEffect ( () => {
    openDB("retail", 1, {
      upgrade(db) {
        db.createObjectStore("details", { keyPath: "email" });
      },
    });
  }, [] );

  useEffect(() => {
    
    window.addEventListener( "afterunload", (async() => { await  getting_data_from_idb();await update_mysql_with_idb(); delete_from_idb();} )) ;
  },[]);

    
  const latest_data = async () => {
    const db = await openDB("retail", 1);
    const data = await db.getAll("details");
    return data;
  };

  const getting_data_from_idb = async () => {
    const newdata = await latest_data();
    setlists1(newdata);
  };

  const update_mysql_with_idb = async () => {

    if(navigator.onLine)
  {  
    lists1.map( (info) => {
      if( info.mysql_sync === false )
      {
        const mysql_data = {
          name: info.name,
          email: info.email,
          contact_number1: info.contact_number1,
          contact_number2: info.contact_number2,
          retail_size: info.retail_size,
          it_automation: info.it_automation,
          no_of_mobile: info.no_of_mobile,
          no_of_tab: info.no_of_tab,
          no_of_computer: info.no_of_computer,
          no_of_printer: info.no_of_printer,
          no_of_scanner: info.no_of_scanner,
          latitude: info.latitude,
          longitude: info.longitude,
        };

        fetch("http://127.0.0.1:8000/retail/create/", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(mysql_data),
        })
          .then(async(response) => {
            if (response.status >= 400 && response.status <= 599) {
              const temp = error + " 400 BAD REQUEST, ERROR FOR RETAIL NAME " + info.name + " EMAIL ID - " + info.email + "DATA NOT ADDED, PLS RE-ENTER PROPERLY" ;
              
              const json_data = await response.json();
              console.log("mysql response", json_data.email);
              seterror(json_data.email);

              const new_delete = {
                email: info.email, 
                mysql_sync: info.mysql_sync
              } ;                

              const update_delete  = [...deletefromidb, new_delete ] ;

              setdeletefromidb(update_delete);
            } else {

              const temp = error + " " + info.email + " added to mysql" ;
              seterror(temp);

              const indexeddb_data = {
                name: info.name,
                email: info.email,
                contact_number1: info.contact_number1,
                contact_number2: info.contact_number2,
                retail_size: info.retail_size,
                it_automation: info.it_automation,
                no_of_mobile: info.no_of_mobile,
                no_of_tab: info.no_of_tab,
                no_of_computer: info.no_of_computer,
                no_of_printer: info.no_of_printer,
                no_of_scanner: info.no_of_scanner,
                latitude: info.latitude,
                longitude: info.longitude,
                mysql_sync: true,
              }
              const db = await openDB("retail", 1);
              db.put("details", indexeddb_data)
              .then((res) => {
                console.log(res);

                const temp = error + " " + info.email + " mysql_sync changed to true" ;
                seterror(temp);
                setsubmitted(true);

              });
              
            }
          })
          .catch((err) => {
            console.log(err);
          });
      }
    } )}
  }
  const delete_from_idb =() => {
    deletefromidb.map( async(item) => {
      const db = await openDB("retail", 1);
      if (await db.get("details", item.email)) 
      {
        await db.delete("details", item.email);
      }
    } )
  };


    // const getdata_from_mysql = () => { 
    //   fetch('http://127.0.0.1:8000/retail/list/')
    //     .then((res) => res.json())
    //     .then((res) => {
    //       console.log(res);
    //       setfetch_Data_from_MySQL(res);
    //     })
    // }

    // const add_mysql_data_in_idb =async () => {
    //   const db = await openDB("retail", 1);
    //   fetch_Data_from_MySQL.map(async (info) => {
   
    //     const mysql_data = {
    //       name: info.name,
    //       email: info.email,
    //       contact_number1: info.contact_number1,
    //       contact_number2: info.contact_number2,
    //       retail_size: info.retail_size,
    //       it_automation: info.it_automation,
    //       no_of_mobile: info.no_of_mobile,
    //       no_of_tab: info.no_of_tab,
    //       no_of_computer: info.no_of_computer,
    //       no_of_printer: info.no_of_printer,
    //       no_of_scanner: info.no_of_scanner,
    //       latitude: info.latitude,
    //       longitude: info.longitude,
    //       mysql_sync: true
    //     };
  
    //     if (await db.get("details", info.email)) 
    //     {
    //     }
    //     else
    //     {
    //       db.add("details", mysql_data)
    //       .then((res) => {
    //         console.log(res);
    //       }).catch((err) => {
    //         console.log(err);
    //         seterror(err);
    //       });
    //     }
    //   } )
    // }

  const HandleLiveCapture = (image) => 
  {
    setlive_capture(image);
    setimage_upload();
  }

  const getlatlog = async (lat, long) => {
    setlatitude(lat);
    setlongitude(long);
    setlocated(latitude && longitude ? true : false);
  };

  // const handlefilesubmit = async (e) => {
  //   for (let index = 0; index < e.target.files.length; index++) {
  //     image_array.push(e.target.files[index]);
  //   }
  //   console.log(image_array);
  //   setimages(image_array);
  // };

  const handlesubmit = async (e) => {
    e.preventDefault();
    seterror("");
    setsubmitted(false);
    const db = await openDB("retail", 1);

    if (located) {
      if (await db.get("details", email)) {
        seterror("Email ID already registered");
      } else if (contact1 === contact2) {
        seterror("Both the contact number can't be same");
      } else if (retailsize <= 0) {
        const temp = "Retail size can't be " + retailsize;
        seterror(temp);
      } else if (!mobile && !tab && !computer && !printer && !scanner) {
        const temp = "Please choose the machine";
        seterror(temp);
      } else if (mobile && nomobile <= 0) {
        const temp = "Number of mobiles can't be " + nomobile;
        seterror(temp);
      } else if (tab && notab <= 0) {
        const temp = "Number of tabs can't be " + notab;
        seterror(temp);
      } else if (computer && nocomputer <= 0) {
        const temp = "Number of computers can't be " + nocomputer;
        seterror(temp);
      } else if (printer && noprinter <= 0) {
        const temp = "Number of printers can't be " + noprinter;
        seterror(temp);
      } else if (scanner && noscanner <= 0) {
        const temp = "Number of scanners can't be " + noscanner;
        seterror(temp);
      } else if (!(image_upload || live_capture)) {
        const temp = " Please capture a live image or upload image ";
        seterror(temp);
      } else {
        const mysql_data = {
          name: name,
          email: email,
          contact_number1: contact1,
          contact_number2: contact2,
          retail_size: retailsize,
          it_automation: itautomation,
          no_of_mobile: nomobile,
          no_of_tab: notab,
          no_of_computer: nocomputer,
          no_of_printer: noprinter,
          no_of_scanner: noscanner,
          latitude: latitude,
          longitude: longitude,
        };

        const indexeddb_data = {
          name: name,
          email: email,
          contact_number1: contact1,
          contact_number2: contact2,
          retail_size: retailsize,
          it_automation: itautomation,
          no_of_mobile: nomobile,
          no_of_tab: notab,
          no_of_computer: nocomputer,
          no_of_printer: noprinter,
          no_of_scanner: noscanner,
          latitude: latitude,
          longitude: longitude,
          mysql_sync: true,
        };

        
        if (navigator.onLine) {
          fetch("http://127.0.0.1:8000/retail/create/", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify(mysql_data),
          })
            .then(async(response) => {
              if (response.status >= 400 && response.status <= 599) {
                const json_data =await response.json();
                console.log("mysql response", json_data);
                const temp = "response status " + response.status + ", " + json_data.email ;
                seterror(temp);
                
              } else {

                db.add("details", indexeddb_data).then((res) => {
                  console.log(res);
                });
                seterror("Retail Information Successfully Submitted");
                setsubmitted(true);
              }
            })
            .catch((err) => {
              console.log(err);
              seterror(err);
            });
        } else {
          console.log("OFFLINE");

          const indexeddb_data_sync_false = {
            name: name,
            email: email,
            contact_number1: contact1,
            contact_number2: contact2,
            retail_size: retailsize,
            it_automation: itautomation,
            no_of_mobile: nomobile,
            no_of_tab: notab,
            no_of_computer: nocomputer,
            no_of_printer: noprinter,
            no_of_scanner: noscanner,
            latitude: latitude,
            longitude: longitude,
            mysql_sync: false,
          };

          db.add("details", indexeddb_data_sync_false).then((res) => {
            console.log(res);
          });
          seterror("Retail Information Successfully Submitted");
          setsubmitted(true);
        }
      }
    } else {
      seterror("Please select your location");
    }
  };

  return (
    <div>
      <form className="getdata" onSubmit={handlesubmit}>
        <span className="heading">Enter Retail Information</span>

        <div className="lbl_ipt">
          <label className="label">Name*</label>
          <input
            className="input"
            type="text"
            onChange={(e) => {
              setname(e.target.value);
            }}
            required
          />
        </div>

        <div className="lbl_ipt">
          <label className="label">E-Mail*</label>
          <input
            className="input"
            type="email"
            onChange={(e) => {
              setemail(e.target.value);
            }}
            required
          />
        </div>

        <div className="lbl_ipt">
          <label className="label contact_font">Contact Number 1*</label>
          <input
            className="input"
            placeholder="10 digit"
            type="tel"
            pattern="[0-9]{10}"
            onChange={(e) => {
              setcontact1(Number(e.target.value));
            }}
            required
          />
        </div>

        <div className="lbl_ipt">
          <label className="label contact_font">Contact Number 2</label>
          <input
            className="input"
            placeholder="10 digit"
            type="tel"
            pattern="[0-9]{10}"
            onChange={(e) => {
              setcontact2(Number(e.target.value));
            }}
          />
        </div>

        <div className="lbl_ipt">
          <label className="label">Retail Size*</label>
          <input
            className="input"
            type="number"
            placeholder="in sq feet area"
            onChange={(e) => {
              setretailsize(Number(e.target.value));
            }}
            required
          />
        </div>

        <div className="lbl_ipt">
          <label className="label">IT Automation*</label>
          <select
            value={itautomation}
            onChange={(e) => {
              setitautomation(e.target.value);
            }}
            required
          >
            <option value="Automated">Automated</option>
            <option value="Non-Automated">Non-Automated</option>
          </select>
        </div>

        <div className="slt_e-comm">
          <label className="label">Types of Machine</label>

          <div className="align_chbox_lbl">
            <label className="machine_css">
              <input
                type="checkbox"
                value={mobile}
                onChange={(e) => {
                  setmobile(e.currentTarget.checked);
                }}
              />
              mobile
            </label>
          </div>
          {mobile && (
            <input
              className="input"
              type="number"
              onChange={(e) => {
                setnomobile(Number(e.target.value));
              }}
              placeholder="Number of Devices"
              required={mobile}
            />
          )}

          <br />
          <div className="align_chbox_lbl">
            <label className="machine_css">
              <input
                type="checkbox"
                value={tab}
                onChange={(e) => {
                  settab(e.currentTarget.checked);
                }}
              />
              tab
            </label>
          </div>
          {tab && (
            <input
              className="input"
              type="number"
              onChange={(e) => {
                setnotab(Number(e.target.value));
              }}
              placeholder="Number of Devices"
              required={tab}
            />
          )}

          <br />
          <div className="align_chbox_lbl">
            <label className="machine_css">
              {" "}
              <input
                type="checkbox"
                value={computer}
                onChange={(e) => {
                  setcomputer(e.currentTarget.checked);
                }}
              />
              computer
            </label>
          </div>
          {computer && (
            <input
              className="input"
              type="number"
              onChange={(e) => {
                setnocomputer(Number(e.target.value));
              }}
              placeholder="Number of Devices"
              required={computer}
            />
          )}

          <br />
          <div className="align_chbox_lbl">
            <label className="machine_css">
              <input
                type="checkbox"
                value={printer}
                onChange={(e) => {
                  setprinter(e.currentTarget.checked);
                }}
              />
              printer
            </label>
          </div>
          {printer && (
            <input
              className="input"
              type="number"
              onChange={(e) => {
                setnoprinter(Number(e.target.value));
              }}
              placeholder="Number of Devices"
              required={printer}
            />
          )}

          <br />
          <div className="align_chbox_lbl">
            <label className="machine_css">
              <input
                type="checkbox"
                value={scanner}
                onChange={(e) => {
                  setscanner(e.currentTarget.checked);
                }}
              />
              scanner
            </label>
          </div>
          {scanner && (
            <input
              className="input"
              type="number"
              onChange={(e) => {
                setnoscanner(Number(e.target.value));
              }}
              placeholder="Number of Devices"
              required={scanner}
            />
          )}

          <br />
        </div>

        <GoogleMaps AddLatLong={getlatlog} />

        <CameraModule AddLiveImage={HandleLiveCapture} />

        <div>
          { live_capture && 
          <img src={ live_capture } alt="live_capture" />
          }
        </div>

        <div className="lbl_ipt">
          <label className="label">Photo</label>
          <input
            className="input"
            type="file"
            onChange={(e) => { setimage_upload(URL.createObjectURL(e.target.files[0])) ; setlive_capture() ;  }}
          />
        </div>

        <div>
        { image_upload && 
          <img src={image_upload} className="image_upload_style" alt="image_upload" />
        }
        </div>

        <label className={`label ${submitted ? "green" : "error_display"} `}>
          {error}
        </label>

        <div className="lbl_ipt form_btn">
          <button type="submit" className="form_button">
            Submit
          </button>
        </div>
      </form>

      <div>
        {/* {lists1.length > 0 ? (
          <>
          <h1>LIST1</h1>
            {lists1.map((task) => (
              <div key={task.email}>
                <p> Name - {task.name} </p>
                <p> Email - {task.email} </p>
                <p> SYNC - {task.mysql_sync && "true"} </p>
              </div>
            ))}
          </>
        ) : (
          "NO DATA "
        )} */}

          {/* {deletefromidb.length > 0 ? (
          <>
          <h1>IDB DELETE</h1>
          { deletefromidb.map( (item) => (
            <div key={item}>
            <p>{item.email}  </p>
            <p>{! item.mysql_sync && "false"}  </p>
            </div>
          ) ) }
          </> 
          ) : (
            "NO IDB " )} */}

          {/* { fetch_Data_from_MySQL.length > 0 ? (
             <>
             <h1>MYSQL DATA</h1>
             { fetch_Data_from_MySQL.length + ' DATAS' }
             { fetch_Data_from_MySQL.map( (item, index) => {
               return <div key={index}>
                 <p>{item.name}</p>
                 <p>{item.email}</p>
               </div>
             } ) }
             </>
             ) : ( "NO MYSQL DATA " ) }  */}

       
        {name} <br />
        {email}
