import React, { useState } from "react";
import { useEffect } from "react";
import { openDB, deleteDB, wrap, unwrap } from "idb";
import './style.css';
import GoogleMaps from "./GoogleMaps";

const Main =() => {
    

    const [name, setname] = useState("") ;
    const [email, setemail] = useState("") ;
    const [contact1, setcontact1] = useState("") ;
    const [contact2, setcontact2] = useState("") ;
    const [retailsize, setretailsize] = useState("") ;
    const [itautomation, setitautomation] = useState("") ;
    const [mobile, setmobile] = useState(false);
    const [tab, settab] = useState(false);
    const [computer, setcomputer] = useState(false);
    const [printer, setprinter] = useState(false);
    const [scanner, setscanner] = useState(false);
    const [latitude, setlatitude] = useState("");
    const [longitude, setlongitude] = useState("");
    const [lists1, setlists1] = useState([]) ; 
    const [updatedmark, setupdatedmark] = useState("");
    const [togglemark, settogglemark] = useState("");

    var data="";
    var datas="";
    var image_array=[];

    openDB('retail', 1, {
        upgrade(db) {
          db.createObjectStore('details', { keyPath: 'email' });
        },
      });

    //   const start = openDB('retail',1);
    //   data = start.getAll('details') ;
    //     setlists1(data);
        

    //   useEffect ( () => {
    //       const updateval = async () => {
    //           const new_data = await latest_data()  
    //           setlists1(new_data)
    //       }
    //       updateval() 
    //   },[lists1] )

    useEffect ( () => {
        const getting_data =  async () => {
            const newdata =await latest_data() ;
            setlists1(newdata) ;

        }
        getting_data(); 
        
    } )

      const latest_data = async () => {
        const db = await openDB('retail',1);
        data = await db.getAll('details') ;
        return data ;
      }

      const deletestudent = async (email) => {
        const db = await openDB('retail',1);
        await db.delete('details', email);

        data = await db.getAll('details') ;
        console.log(data);
        setlists1(data);
        console.log(lists1);
      }

      const handletogglemark = (email) => {
          if( email === togglemark )
          {
            settogglemark("");
          }
          else
          {
          settogglemark(email);
          }
      }

      const updatemark = async (e, task) => {
        e.preventDefault() ;
        console.log(updatedmark)
        console.log(task);

        const db = await openDB('retail',1);
        db.put( 'details', { name: task.name, email: task.email, dob: task.dob, rollno: task.rollno, mark: updatedmark } )
        .catch((err) => { console.log(err) } ) ;

        data = await db.getAll('details') ;
        setlists1(data);


      }

      const handlefilesubmit = async(e) => {
        
        for (let index = 0; index < e.target.files.length; index++) {
          image_array.push(e.target.files[index])
        }
        console.log(image_array)
      }

    const handlesubmit =async (e) => {
        e.preventDefault() ;
        const db = await openDB('retail',1);
        
        
        
        // db.add( 'details', { name: name, email: email, dob: dob, rollno: rollno, mark: mark } )
        // .catch((err) => { console.log(err) } ) ;

        data = await db.getAll('details') ;
        console.log(data);
        setlists1(data);
        console.log(lists1);

    }
    
  return (
      <div>
          <form className="getdata" onSubmit={handlesubmit}>

            <span className="heading">Enter Retail Information</span>

          <div className="lbl_ipt">    
          <label>Name</label>
          <input type='text' onChange={ (e) => { setname(e.target.value) } } required />
          </div>

          <div className="lbl_ipt">
          <label>E-Mail</label>
          <input type='email' onChange={ (e) => { setemail(e.target.value) } } required />
          </div>

          <div className="lbl_ipt">
          <label>Contact Number 1</label>
          <input type='number' onChange={ (e) => { setcontact1(e.target.value) } } required />
          </div>

          <div className="lbl_ipt">
          <label>Contact Number 2</label>
          <input type='number' onChange={ (e) => { setcontact2(e.target.value) } } />
          </div>

          <div className="lbl_ipt">
          <label>Size of the Retail</label>
          <input type='number' placeholder="in sq feet area" onChange={ (e) => { setretailsize(e.target.value) } } required />
          </div>

          <div className="lbl_ipt">
          <label>IT Automation</label>
          <select onChange={ (e) => { setitautomation(e.target.value) } } required>
            <option value="Automated">Automated</option>
            <option value="Non-Automated">Non-Automated</option>
          </select>
          </div>

          <div className="slt_e-comm">
          <label>Types of Machine</label>
                
          <div className="e-comm_ind">
            <input type="checkbox" value={mobile} onChange={ (e) => { setmobile(e.currentTarget.checked) }  } />
            <label className="e-comm_ind_txt" > MOBILE</label>
            { mobile &&   <input type='number' placeholder="Enter Number of Devices" /> }
          </div>
                
          <div className="e-comm_ind">
            <input type="checkbox"   />
            <label > TAB</label>
            <input type='number' placeholder="Enter Number of Devices" />
          </div>

          <div className="e-comm_ind">
            <input type="checkbox"   />
            <label > COMPUTER</label>
            <input type='number' placeholder="Enter Number of Devices" />
          </div>

          <div className="e-comm_ind">
            <input type="checkbox"   />
            <label > PRINTER</label>
            <input type='number' placeholder="Enter Number of Devices" />
          </div>

          <div className="e-comm_ind">
            <input type="checkbox"   />
            <label > SCANNER</label>
            <input type='number' placeholder="Enter Number of Devices" />
          </div>
        </div>

        <GoogleMaps />

        <div className="lbl_ipt">
          <label>Photo</label>
          <input type='file' multiple onChange={ (e) => { handlefilesubmit(e) } } required />
          </div>

          <div className="lbl_ipt form_btn">
          <button type="submit" className="form_button">Submit</button>
          </div>

          </form>

          <div className="display_students">

           { name } <br />
           { email }<br />
           { contact1 }<br />
           { contact2 }<br />
           { retailsize }<br />
           { itautomation }<br />
           { image_array }<br />
           { mobile && <p>mobile</p> } <br />

           
          </div>

      </div>
  ) 
};

export default Main;
